mapper.insert:
INSERT INTO products (user_id, name, description, cate_code, price, stock)
VALUES (#{userId},#{name},#{description},#{cate_code},#{price},#{stock});



    SELECT LAST_INSERT_ID();

mapper.select:
SELECT *
from products
ORDER BY created_at DESC;

SELECT order_id AS orderId, user_id AS userId, order_state AS orderState, order_date AS orderDate FROM orders
WHERE user_id LIKE concat('%',#{keyword}, '%')
ORDER BY order_date DESC limit #{skip}, #{amount}
