mapper.select:
SELECT id AS productId, name, price
FROM products
WHERE id = #{id};

SELECT id AS productId, price
FROM products
WHERE id = #{id}

mapper.update:
UPDATE products
SET stock            = #{stock},
    cumulative_sales = #{cumulative_sales}
WHERE id = #{id}

mapper.delete:
DELETE
FROM carts
WHERE user_id = #{user_id}
  AND product_id = #{product_id}

mapper.update:
UPDATE orders
SET order_state = '주문취소'
WHERE order_id = #{orderId}
