mapper.select:
SELECT id AS productId, name, price
FROM products
WHERE id = #{id};

SELECT id AS productId, price
FROM products
WHERE id = #{id}

mapper.update:
UPDATE products
SET stock = #{stock}
WHERE id = #{id}

mapper.delete:
DELETE
FROM carts
WHERE user_id = #{user_id}
  AND product_id = #{product_id}
